"use strict";

const 
		request = require('request');


module.exports.sendNotification = (firebaseToken, title, body) => {

	var body = {
		to: firebaseToken,
		notification: {
			title: title,
			body: body
		}
	};

	var options = {
		uri: 'https://fcm.googleapis.com/fcm/send',
		method: 'POST',
		headers: {
			'Authorization': 'key=' + process.env.FIREBASE_SECRET_TOKEN,
			'Content-Type': 'application/json'
		},
		json: body
	};


	request(options, function (err, res, body) {
		if (err) {
			console.log(err);
		};
	});

};