const
 		request = require('request');

module.exports.getTextFileContent = (url, authKey) => {

	return new Promise(function(resolve, reject) {

	    request({
	      uri: url,
	      method: 'GET',
	      headers: { 
	      	'AuthKey': authKey
	      }
	  	}, (err, resp, body) => {
			  return resolve(body);
	  	})		
	  	
	});	

}